import axios from 'axios'
import btoa from 'btoa'

const header = `${process.env.MY_SPORTS_FEEDS_API_KEY}:MYSPORTSFEEDS`
const base64header = btoa(header)

export default axios.create({
  headers: {
    Authorization: `Basic ${base64header}`
  }
})
